const input = $input.first().json;
let transcriptText = '';
let hasTranscript = false;
let detectedLang = 'ko';

try {
  // youtube-transcriptor API 응답 형식: [{transcriptionAsText, transcription, ...}]
  const data = Array.isArray(input) ? input[0] : input;

  if (data && data.transcriptionAsText) {
    // 전체 텍스트 사용
    transcriptText = data.transcriptionAsText
      .replace(/\n/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
    hasTranscript = transcriptText.length > 0;

    // 사용 가능한 언어에서 감지
    if (data.availableLangs && data.availableLangs.length > 0) {
      detectedLang = data.availableLangs[0];
    }
  }
  // 백업: transcription 배열 사용
  else if (data && data.transcription && Array.isArray(data.transcription)) {
    transcriptText = data.transcription
      .map(item => item.subtitle || item.text || '')
      .join(' ')
      .replace(/\n/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();
    hasTranscript = transcriptText.length > 0;
  }
} catch (e) {
  transcriptText = '';
  hasTranscript = false;
}

return [{
  json: {
    transcript_text: transcriptText,
    has_transcript: hasTranscript,
    detected_language: detectedLang
  }
}];

