const items = $input.all();
const videos = items.map(item => ({ video_url: item.json.video_url, cut_number: item.json.cut_number })).sort((a, b) => a.cut_number - b.cut_number);
const videoUrls = videos.map(v => v.video_url);
const firstItem = items[0].json;
return [{ json: { video_urls: videoUrls, video_count: videoUrls.length, ID: firstItem.ID, row_number: firstItem.row_number } }];

const items = $input.all();
let audioPath = '';
let videoUrls = [];
let id = '';
let rowNumber = 0;

for (const item of items) {
  if (item.json.audio_path) {
    audioPath = item.json.audio_path;
    id = item.json.ID;
    rowNumber = item.json.row_number;
  }
  if (item.json.video_urls) {
    videoUrls = item.json.video_urls;
    id = id || item.json.ID;
    rowNumber = rowNumber || item.json.row_number;
  }
}

const dir = `/tmp/videos/yt_recreate_${id}`;

let script = `#!/bin/bash
set -e
DIR="${dir}"

# 오디오 파일 확인 (이미 29초로 정규화됨)
if [ ! -f "${audioPath}" ]; then
  echo "ERROR: Audio file not found at ${audioPath}"
  exit 1
fi
echo "Audio file exists: ${audioPath}"

`;

// 비디오 클립 다운로드
videoUrls.forEach((url, i) => {
  script += `echo "Downloading clip ${i+1}..."
curl -sL "${url}" -o $DIR/clip_${i+1}.mp4
`;
});

// concat 파일 생성
script += `
> $DIR/concat.txt
`;

videoUrls.forEach((_, i) => {
  script += `echo "file 'clip_${i+1}.mp4'" >> $DIR/concat.txt
`;
});

// 비디오 합치기 및 오디오 병합
script += `
echo "Concatenating videos..."
cd $DIR
ffmpeg -y -f concat -safe 0 -i concat.txt -c copy video_only.mp4

echo "Merging with audio..."
ffmpeg -y -i video_only.mp4 -i audio.mp3 -c:v copy -c:a aac -shortest final.mp4

FINAL_PATH="$(pwd)/final.mp4"
echo "Final video created: $FINAL_PATH"
echo "$FINAL_PATH"
`;

return [{ json: { assembly_script: script, dir: dir, ID: id, row_number: rowNumber } }];

const aiOutput = $json.output || $json;
const id = $('Set Assembly Result').first().json.ID;
const rowNum = $('Set Assembly Result').first().json.row_number;

// AI가 생성한 메타데이터 사용
const title = aiOutput.title || '숏폼 영상';
const description = aiOutput.description || '';
const tags = aiOutput.tags || ['shorts', '숏폼'];

// Read Final Video에서 binary 데이터 가져오기
const binaryData = $('Read Final Video').first().binary;

return [{
  json: {
    title,
    description,
    tags,
    categoryId: '22',
    privacyStatus: 'private',
    ID: id,
    row_number: rowNum
  },
  binary: binaryData
}];

// YouTube node returns the video data directly
const videoId = $json.uploadId || 'unknown';

let rowNum, id;
try {
  rowNum = $('Prepare Upload Metadata').first().json.row_number;
  id = $('Prepare Upload Metadata').first().json.ID;
} catch (e) {
  rowNum = 0;
  id = 'unknown';
}

// Cost estimation
const ttsCost = 0.016;  // Gemini TTS
const imageCost = 6 * 0.09;  
const videoCost = 6 * 0.275; 
const aiCost = 0.05;  // AI script generation
const totalCost = ttsCost + imageCost + videoCost + aiCost;

return {
  YouTube_Video_ID: videoId,
  Status: 'completed',
  Completed_At: new Date().toISOString(),
  Cost: totalCost.toFixed(3),
  Final_Video_URL: `https://youtube.com/shorts/${videoId}`,
  row_number: rowNum,
  ID: id
};

